class BinOp
{
    public:
        string toBinary(int a)
        {
            string ans = "";
            while(a)
            {
                if(a%2)
                    ans += '1';
                else 
                    ans += '0';
                
                a/=2;
            }
            reverse(all(ans));
            return ans;
        }

        int toInt(string bin)
        {
            reverse(all(bin));
            int ans = 0;
            int len = bin.length();
            for(int i = 0; i < len; i++)
            {
                if(bin[i] == '1')
                    ans += pow(2, i);
            }
            return ans;
        }

};


BinOp bp;


vector<pair<string, pii>> anss;

bool comp(pair<string, pii> a, pair<string, pii> b)
{
    if(abs(a.second.first-a.second.second) == abs(b.second.first-b.second.second))
        return a.first > b.first;
    return abs(a.second.first-a.second.second) <  abs(b.second.first-b.second.second);
}


int go(int i, int a)
{
    if(i > n)
    {
        int x = 0, y = 0;
        for(int p = 1; p <= n; p++)
        {
            if(a&(1<<p))
                x += p*p*p;
            else
                y += p*p*p;
        }
        str = bp.toBinary(a);
        str = str.substr(0, str.length()-1);
        while(str.length() < n)
            str = '0' + str;
        anss.push_back({str, {x,y}});
    }
    else
    {
        go(i+1, a);
        go(i+1, a+(1<<i));
    }
}


